const RecipeCard: React.FC<{ recipe: Recipe; onClick: () => void }> = ({ recipe, onClick }) => (
    <div className="bg-white rounded-xl shadow-md overflow-hidden cursor-pointer hover:shadow-lg transition-shadow" onClick={onClick}>
        <img className="w-full h-48 object-cover" src={recipe.image} alt={recipe.name} />
        <div className="p-4">
            <h3 className="font-bold text-lg text-gray-800">{recipe.name}</h3>
            <p className="text-gray-600 text-sm mt-1">{recipe.description}</p>
        </div>
    </div>
);

const RecipeModal: React.FC<{ recipe: Recipe; onClose: () => void }> = ({ recipe, onClose }) => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-xl shadow-2xl w-full max-w-2xl max-h-[90vh] overflow-y-auto">
            <div className="p-4 flex justify-between items-center border-b">
                <h2 className="text-2xl font-bold text-gray-800">{recipe.name}</h2>
                <button onClick={onClose} className="text-gray-500 hover:text-gray-800"><X size={24} /></button>
            </div>
            <img className="w-full h-64 object-cover" src={recipe.image} alt={recipe.name} />
            <div className="p-6">
                <div className="mb-6">
                    <h3 className="font-semibold text-xl text-emerald-700 mb-2">Ingredientes</h3>
                    <ul className="list-disc list-inside space-y-1 text-gray-700">
                        {recipe.ingredients.map((ing, i) => <li key={i}>{ing}</li>)}
                    </ul>
                </div>
                <div>
                    <h3 className="font-semibold text-xl text-emerald-700 mb-2">Modo de Preparo</h3>
                    <ol className="list-decimal list-inside space-y-2 text-gray-700">
                        {recipe.instructions.map((inst, i) => <li key={i}>{inst}</li>)}
                    </ol>
                </div>
            </div>
        </div>
    </div>
);

const RecipesPage: React.FC = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedRecipe, setSelectedRecipe] = useState<Recipe | null>(null);

    const filteredRecipes = recipesData.filter(recipe =>
        recipe.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="space-y-6">
            <h1 className="text-3xl font-bold text-gray-800">Biblioteca de Receitas</h1>
            <p className="text-gray-600">Mais de 100 receitas Low-Carb para inspirar suas refeições.</p>

            <div className="relative">
                <input
                    type="text"
                    placeholder="Buscar receita..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full p-3 pl-10 border border-gray-300 rounded-lg focus:ring-emerald-500 focus:border-emerald-500"
                />
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" size={20} />
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredRecipes.map(recipe => (
                    <RecipeCard key={recipe.id} recipe={recipe} onClick={() => setSelectedRecipe(recipe)} />
                ))}
            </div>
            
            {filteredRecipes.length === 0 && (
                <div className="text-center py-10">
                    <p className="text-gray-500">Nenhuma receita encontrada.</p>
                </div>
            )}

            {selectedRecipe && <RecipeModal recipe={selectedRecipe} onClose={() => setSelectedRecipe(null)} />}
        </div>
    );
};

export default RecipesPage;